import { Component, Input, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts';

@Component({
  selector: 'app-gauge',
  templateUrl: './gauge.component.html',
  styleUrls: ['./gauge.component.scss'],
})
export class GaugeComponent implements OnInit {
  Highcharts: typeof Highcharts = Highcharts;
  @Input() private maxRank: number = 10000000;
  @Input() public currentRank: number = null;

  public padding = this.maxRank / 10;
  public gauge: Highcharts.Options;

  ngOnInit() {
    this.gauge = {
      chart: {
        type: 'bar',
        margin: [55, 0, 0, 0],
        scrollablePlotArea: {
          minWidth: 320,
        },
      },
      credits: { enabled: false },
      exporting: { enabled: false },
      legend: { enabled: false },
      title: { text: '' },
      xAxis: {
        tickLength: 0,
        lineWidth: 0,
        max: 1,
      },
      yAxis: {
        min: 1 - this.padding,
        max: this.maxRank + this.padding,
        reversed: true,
        gridLineWidth: 0,
        title: { text: '' },
        startOnTick: false,
        endOnTick: false,
        tickPositioner: () => {
          return [1, this.maxRank];
        },
        height: 50,
        labels: {
          padding: 5,
          useHTML: true,
          formatter: this.formatLabel(this.maxRank),
        },
        plotBands: [
          {
            from: 1 - this.padding,
            to: this.maxRank + this.padding,
            color: {
              linearGradient: { x1: 0, x2: 1, y1: 0, y2: 0 },
              stops: [
                [0, '#CB0101'], // start
                [0.2, '#E35704'], // middle
                [1, '#19963C'], // end
              ],
            },
          },
        ],
      },
      series: [
        {
          name: 'Target',
          type: 'scatter',
          data: [[0, this.currentRank]],
          dataLabels: {
            enabled: true,
            padding: 0,
            useHTML: true,
            formatter: function () {
              return '<span class="govuk-heading-xl govuk-!-margin-bottom-2">' + this.y + '</span>';
            },
            crop: false,
            overflow: 'allow',
          },
        },
      ],
      tooltip: {
        enabled: false,
      },
      plotOptions: {
        series: {
          animation: false,
        },
        bar: {},
        scatter: {
          marker: {
            symbol:
              'url( data:image/png;base64,)',
          },
        },
      },
    };
  }

  private formatLabel(max: number): Highcharts.AxisLabelsFormatterCallbackFunction {
    return function () {
      let value;
      if (max === 10000000) {
        value = '';
      } else {
        value = this.value;
      }
      if (this.value === 1) {
        return (
          '<span class="govuk-body govuk-!-margin-bottom-0 govuk-!-margin-right-2">' +
          'Highest ranking ' +
          '<span class="govuk-!-font-weight-bold govuk-!-margin-left-4">' +
          value +
          '</span></span>'
        );
      }
      if (this.value < 0) return;
      return (
        '<span class="govuk-body govuk-!-margin-bottom-0 govuk-!-margin-left-2">' +
        '<span class="govuk-!-font-weight-bold govuk-!-margin-right-4">' +
        value +
        '</span>Lowest ranking</span>'
      );
    };
  }
}
